INSERT INTO `tw_news` VALUES ('5', '少儿编程产品受追捧，这十款产品你不能错过.', '可以转动的轮子已经不酷了，舵机更受到欢迎，让孩子们亲手组装出一只八条腿行走的野兽更棒。（HEXAPOD、Robolink、可以科技、Jimu Robot传感器和信息收集部件增多，从普通的光感传感器和灯泡，到如今的力热声光电多种传感器以及视频音频等多维信息收集终端，孩子们可以用这些编程产品编出许多具有实用性的产品。（WUNDERKIND、SAMLABS、OzOcodes）', '4', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);
