INSERT INTO `tw_news` VALUES ('8', '回望2016，预见2017【视频】', '每一个正在发生的时刻都是历史的转折，作为见证者，站在历史的当下，我们用6分钟，回望2016，预见2017。', '3', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', '2017-04-15', '2017-04-16');
INSERT INTO `tw_news` VALUES ('9', '任务解说｜Scratch编程任务《算法挑战》', '学习编程需要学很多数学知识吗？现实中学习编程绝大多数用到的数学知识并不是很多，不过对我们小朋友来说，最基础的数学知识还是必须要掌握的。', '3', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', '2017-04-15', '2017-04-16');
INSERT INTO `tw_news` VALUES ('10', '用乐高玩具发明写贺卡的机器人', '为了省去手写圣诞卡的麻烦，两位少年使用乐高积木制作出了一部写贺卡的机器人。', '3', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', '2017-04-15', '2017-04-16');
INSERT INTO `tw_news` VALUES ('11', '编程在行动｜2016计算机科学教育周，快快来！', '编程一小时：计算机科学教育周2016年12月5日-11日，活动已在全国各地展开，没机会参与活动就快来这里，学习、参与、赢取证书。', '2', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);
INSERT INTO `tw_news` VALUES ('12', '2016全球编程一小时线下活动【多城市】', '12月5日到11日：北京、上海、成都、天津、济南、青岛、广州、深圳、南宁、南京等十多个城市的“编程一小时”活动欢迎参加。', '2', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);
INSERT INTO `tw_news` VALUES ('13', 'ROOT编程机器人（又一款学习编程机器人发起众筹）', '据了解，ROOT编程机器人设计用于在墙壁，桌面和磁性表面上移动，并且能够制作游戏和进行编程挑战，从而加强计算思维。', '4', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);
INSERT INTO `tw_news` VALUES ('14', 'scratch计算偶数之和【视频】', '用Scratch实现随意输入一个整数，要求能够计算出小于这个数的所有偶数之和。', '4', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);
INSERT INTO `tw_news` VALUES ('15', 'Scratch编程任务《算法挑战》', '本次任务初级目标：变量，循环的灵活使用并能够与我们的数学知识相结合；高级目标，通过分析思考，去提高程序的执行速度（效率），设计出更优的方案，重点在于思考分析问题的能力提高。', '4', '0', '软易教育', '2017-04-17 星期一', '0', 'admin', '2017-04-17 星期一', null, null);

